import styled from 'styled-components';

interface PropsColor {
  color: string;
}

export const Container = styled.div`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div`
  background: #fff;
  border: 1px solid #eee;

  width: 100%;
  max-width: 660px;
  padding: 40px;

  display: flex;
  flex-direction: column;

  align-items: center;
  position: relative;

  h1 {
    color: #495464;

    margin-top: 40px;
    text-transform: uppercase;
  }

  span {
    background: rgba(0, 0, 0, 0.3);
    padding: 8px 16px;
    font-weight: 500;
    font-size: 18px;
  }

  button {
    width: 100%;
    padding: 16px 0;
    margin-top: 40px;
    border: 0;
    background: #373a40;
    color: #fff;
    font-weight: 500;
    font-size: 22px;

    text-transform: uppercase;
    display: flex;
    align-items: center;
    justify-content: center;

    svg {
      margin-right: 8px;
    }
  }
`;

export const Color = styled.div<PropsColor>`
  position: absolute;
  background: ${props => props.color};

  padding: 80px;
  border-radius: 50%;
  top: 0;
  margin-top: -100px;
  border: 1px solid #eee;
`;
