import styled from 'styled-components';

export const Container = styled.div`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div`
  background: #fff;
  border: 1px solid #eee;

  width: 100%;
  max-width: 660px;
  padding: 40px;

  display: flex;
  flex-direction: column;

  align-items: center;
  position: relative;

  h1,
  h2 {
    color: #495464;
    margin: 16px 0;
  }

  h1 {
    margin-top: 40px;
  }

  button {
    width: 100%;
    padding: 16px 0;
    margin-top: 40px;
    border: 0;
    background: #373a40;
    color: #fff;
    font-weight: 500;
    font-size: 120px;
  }
`;

export const Color = styled.div`
  position: absolute;
  background: red;

  background: red;
  padding: 80px;
  border-radius: 50%;
  top: 0;
  margin-top: -100px;
`;
