import styled from 'styled-components';
import { shade } from 'polished';

interface PropsColor {
  color: string;
}

export const Container = styled.div`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div<PropsColor>`
  background: #fff;
  border: 1px solid #eee;

  width: 100%;
  max-width: 660px;
  padding: 40px;

  display: flex;
  flex-direction: column;

  align-items: center;
  position: relative;

  h1 {
    color: #495464;
    margin-top: 40px;
    text-transform: uppercase;
  }

  div {
    display: flex;
    flex-direction: row;
    align-items: center;

    svg {
      color: #ccc;
      transition: color 0.5s;

      &:hover {
        color: #ea1d48;
      }
    }

    span {
      padding: 8px 16px;
      font-weight: 500;
      font-size: 18px;
      margin: 16px 0;

      transition: color 0.5s;

      color: ${props => props.color};
    }
  }

  button {
    width: 100%;
    padding: 16px 0;
    margin-top: 1px;
    border: 0;
    background: #f1f6f9;
    color: #424242;
    font-weight: 500;
    font-size: 22px;
    border: 1px solid #eee;

    text-transform: uppercase;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.5s;

    svg {
      margin-right: 8px;
    }

    &:hover {
      background: ${shade(0.03, '#f1f6f9')};
    }
  }
`;

export const Color = styled.div<PropsColor>`
  position: absolute;
  background: ${props => props.color};
  transition: background-color 0.5s;

  padding: 80px;
  border-radius: 50%;
  top: 0;
  margin: 0;
  margin-top: -100px;
  border: 1px solid #eee;
`;
