import styled from 'styled-components';

export const Container = styled.div`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div`
  background: #495464;
  width: 100%;
  max-width: 660px;
  padding: 80px;

  display: flex;
  justify-content: center;
  position: relative;
`;

export const Color = styled.div`
  position: absolute;
  background: red;

  background: red;
  padding: 80px;
  border-radius: 50%;
  top: 0;
  margin-top: 60px;
`;
